import { Component, EventEmitter, Output } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

//Importamos lo necesario para construir el formulario

@Component({
  selector: 'app-login-form',
  templateUrl: './login-form.component.html',
  styleUrls: ['./login-form.component.scss']
})
export class LoginFormComponent {

  loginForm: FormGroup= new FormGroup({});
  @Output() loginAction: EventEmitter<{}>= new EventEmitter <{}>()

  constructor(private formBuilder: FormBuilder){

  }

  ngOnInit(): void {

    this.loginForm= this. formBuilder.group({
      email: ['', Validators.compose([Validators.required, Validators.email])],
      password: ['', Validators.required],
    })
  }

  get email(){
    return this.loginForm.get('email');
  }

  get password(){
    return this.loginForm.get('password');
  }

  //Submit del formulario de Login
  submitLogin(){
    if(this.loginForm.valid){
    console.table(this.loginForm.value);
    this.loginAction.emit(log)
    //TODO petición a authService
    this.loginForm.reset();
  }
  }



}
<!-- Montamos Login Form haciendo uso de Componentes Material -->
<mat-card>

  <!-- Cabecera de la tarjeta -->
  <mat-card-header>
    <mat-card-title>
      Login
    </mat-card-title>
    <mat-card-subtitle>
      Accede a tu cuenta
    </mat-card-subtitle>
  </mat-card-header>

  <!-- Contenido de la tarjeta -->
  <mat-card-content>
    <!-- Formulario de Login -->
    <form [formGroup]="loginForm" (submit)="submitLogin()">

      <!-- EMAIL -->
      <mat-form-field>
        <input matInput placeholder="Email" formControlName="email">
        <mat-error *ngIf="email?.errors?.['required']&& email?.touched">
          El campo email es obligatorio
        </mat-error>
        <mat-error *ngIf="email?.errors?.['email']&& email?.touched">
          El formato email no es correcto
        </mat-error>
      </mat-form-field>

      <!-- PASSWORD -->
      <mat-form-field>
        <input type="password" matInput placeholder="Password" formControlName="password">
        <mat-error *ngIf="password?.errors?.['required']&& password?.touched">
          El campo email es obligatorio
        </mat-error>

      </mat-form-field>

      <!-- Botón para hacer submit del formulario -->
      <!-- Deshabilitado si el formulario no es valido -->
      <button type="submit" mat-raised-button color="primary" [disabled]="loginForm.invalid" >Login</button>


    </form>

  </mat-card-content>

</mat-card>
